terms.map:
function(doc) {
    if (doc) {
        if (doc && doc.type && doc.type.toLowerCase() === 'term') {
            emit(doc.uniqueId, doc);
        }
    }
}

transforms.map:
function(doc) {
    if (doc) {
        if (doc && doc.type && (doc.type.toLowerCase() === 'transform')) {
            emit(doc.uniqueId, doc);
        }
    }
}

conditions.map:
function(doc) {
    if (doc) {
        if (doc && doc.type && doc.type.toLowerCase() === "condition") {
            emit(doc.uniqueId, doc);
        }
    }
}

children.map:
function(doc) {
    if (doc && doc.type && (doc.type.toLowerCase() === "alias" || doc.type.toLowerCase === "transform" || doc.type.toLowerCase === "translation")) {
        var key = doc.aliasOf;
        if (doc.type.toLowerCase === "translation") {
            key = doc.translationOf;
        }
        emit(key, doc);
    }
}

aliases.map:
function(doc) {
    if (doc && doc.type && doc.type.toLowerCase() === "alias") {
        emit(doc.uniqueId, doc);
    }
}
